    override fun crearSetDeDatosIniciales() {
        for(pais in paises) {
            val precioRandom = Random.nextDouble(9999.0)
            val fechaVueloRandom = Calendar.getInstance()
            fechaVueloRandom.add(Calendar.DAY_OF_YEAR, Random.nextInt(365))
            val vuelo = Vuelo(pais, paises.random(), precioRandom, fechaVueloRandom.time)
            vuelo.duracion = Random.nextInt(100)
            vueloService.guardar(vuelo)
        }
    }

    override fun eliminarTodo() {
        TODO("Not yet implemented")
    }

    fun cantidadPaises() = paises.count()
}